function Tabs(){const t=document.querySelectorAll(".tabs__item");t.forEach(t=>{t.addEventListener("click",e=>{const c=e.target.getAttribute("href").split("#")[1],l=document.querySelectorAll(".tabs__content"),s=t.parentElement.childNodes;e.preventDefault();for(let t=0;t<s.length;t++)t%2==1&&s[t].classList.remove("active");t.classList.add("active"),l.forEach(t=>{const e=t.getAttribute("id"),l=t;l.style.display=c===e?"flex":"none"})})}),t[0].click()}Tabs();